// Eingabedaten validieren und strukturieren
const inputData = $input.first().json;

// Zeitstempel und eindeutige ID generieren
const anfrage_id = 'BER-' + Date.now() + '-' + Math.random().toString(36).substr(2, 5).toUpperCase();
const zeitstempel = new Date().toLocaleString('de-DE', {
  timeZone: 'Europe/Berlin',
  year: 'numeric',
  month: '2-digit', 
  day: '2-digit',
  hour: '2-digit',
  minute: '2-digit'
});

// Priorit√§t basierend auf Interessensbereich bestimmen
let prioritaet = 'Normal';
if (inputData['Interessensbereich'] === 'Preisanfrage') prioritaet = 'Hoch';
if (inputData['Interessensbereich'] === 'Technische Beratung') prioritaet = 'Sehr Hoch';

// E-Mail Validierung
const emailRegex = /^[^\s@]+@[^\s@]+\.[^\s@]+$/;
const istGueltigeEmail = emailRegex.test(inputData['E-Mail-Adresse']);

// Name Validierung
const istGueltigerName = inputData['Vollst√§ndiger Name'] && inputData['Vollst√§ndiger Name'].length >= 2;

if (!istGueltigeEmail || !istGueltigerName) {
  return [{
    json: {
      fehler: true,
      fehlergrund: !istGueltigeEmail ? 'Ung√ºltige E-Mail-Adresse' : 'Name zu kurz',
      eingabedaten: inputData
    }
  }];
}

// Strukturierte Ausgabe
return [{
  json: {
    anfrage_id: anfrage_id,
    zeitstempel: zeitstempel,
    prioritaet: prioritaet,
    kundendaten: {
      vollstaendiger_name: inputData['Vollst√§ndiger Name'],
      email_adresse: inputData['E-Mail-Adresse'],
      unternehmen: inputData['Unternehmen'] || 'Nicht angegeben',
      telefonnummer: inputData['Telefonnummer'] || 'Nicht angegeben',
      kontaktpraeferenz: inputData['Kontaktpr√§ferenz']
    },
    anfrage_details: {
      interessensbereich: inputData['Interessensbereich'],
      detaillierte_anfrage: inputData['Detaillierte Anfrage'],
      ursprungsformular: 'Kundenberatungsformular'
    },
    verarbeitung_status: 'Eingegangen',
    fehler: false
  }
}];

// Interne Team-Benachrichtigung vorbereiten
const emailData = $input.first().json;
const anfrageData = emailData.anfrage_daten;

// Priorit√§ts-basierte Betreffzeile
let prioritaetsPrefix = '';
if (anfrageData.prioritaet === 'Hoch') prioritaetsPrefix = 'üü° HOCH: ';
if (anfrageData.prioritaet === 'Sehr Hoch') prioritaetsPrefix = 'üî¥ SEHR HOCH: ';

// Interne E-Mail Template
const interneEmailHTML = `
