    betreff: `${prioritaetsPrefix}Neue Kundenanfrage - ${anfrageData.anfrage_details.interessensbereich} von ${anfrageData.kundendaten.vollstaendiger_name}`,
    html_inhalt: interneEmailHTML,
    prioritaet: anfrageData.prioritaet,
    anfrage_id: anfrageData.anfrage_id,
    kunde_email: anfrageData.kundendaten.email_adresse
  }
}];
